struct SaturatedEdge<T1, T2>{
    from: T1, to: T1, wt: T2,
}
struct Edge<T>{
    from: T, to: T,
}
trait SimpleGraph<T> {
    fn addNode(&self, e: T);
}
struct Graph<T>{
    nodes: i64, adj: Vec<Vec<T>>,
}
impl SimpleGraph<T> for Graph<T>{
    fn addNode(&self, e: Edge<T>) {
        self.adj[e.from].push(e.to);
    }
}

trait SimpleGraph<T> {
    fn add_node(&mut self, e: T);
}
impl<T> SimpleGraph<T> for Graph<T>
    where T:Copy, usize: From<T>
    {
    fn add_node(&mut self, e: T) {
        self.adj[usize::from(e)].push(e);
    }
}
