const INPUT_UNION_SIZE = max(MouseInput |> sizeof, KeyboardInput |> sizeof, HardwareInput |> sizeof)
struct InputUnion
    inner::SVector{INPUT_UNION_SIZE, UInt8}
end

function InputUnion(input::Union{MouseInput, KeyboardInput, HardwareInput})
    bytes_ref = Ref(@SVector(fill(zero(UInt8), INPUT_UNION_SIZE)))
    unsafe_store!(Ptr{typeof(input)}(pointer_from_objref(bytes_ref)), input)

    InputUnion(bytes_ref[])
end
