Base.length(f::Foo) = length(f.a)
function Base.iterate(f::Foo, state=1)
    state > length(f) && return nothing
    if state == 2
        return (f.a[state], state+1) # can this be skipped?
    else
        return (f.a[state], state+1)
    end
end

for a_i in Foo(rand(3))
    @show a_i
end

