using StructArrays

import StructArrays: staticschema, createinstance
import Base: getproperty, propertynames, +

struct A{N,T}
    data::NTuple{N,T}
end

+(a::A{N,T}, b::A{N,T}) where{N,T} = A{N,T}(a.data .+ b.data)

propertynames(::A{N,T}) where {N,T} = ntuple(i -> Symbol(:idx_, i), Val{N}())

@generated function getproperty(x::A{N,T}, s::Symbol) where {N,T}
    quote
        $(Expr(:meta, :inline))
        $([:(s === Symbol(:idx_, $i) && return getfield(x, 1)[$i]) for i = 1:N]...)
        return getfield(x, s)
    end
end

@generated function staticschema(::Type{A{N,T}}) where {N,T}
    quote
        NamedTuple{$(ntuple(i -> Symbol(:idx_, i), Val{N}())), NTuple{$N,$T}}
    end
end

