function test(a::Zygote.Buffer, x::Vector, P)
    mysum = 0.0
    for j in eachindex(x)
        Zygote.@ignore(a.freeze = false)
        for i = 1:length(a)
            a[i] = P*j
        end
        mysum += sum(copy(a))*x[j]
    end
    return mysum
end
# run to see that gradient computation is in fact correct
function verify()
    a = Zygote.Buffer(zeros(10))
    x = rand(10); dx = zeros(10); dx[3] = 0.0001
    @show((test(a, x, 2.0001)-test(a, x, 2))/0.0001)
    @show((test(a, x + dx, 2)-test(a, x, 2))/0.0001)
    @show gradient(test, a, x, 2)
end

