using Makie 

@recipe(MyPlot, x, f) do scene 
    Attributes(
        mycolor = :red 
    )
end

function AbstractPlotting.plot!(plt::MyPlot) 
    xx = plt[:x] 
    ff = plt[:f]   # `ff` is not a function. It is a vector of floats. 
    @show xx 
    @show ff 
    lines!(plt, xx, ff, color=plt.mycolor) 
    plt
end

myplot(collect(0 : 0.1 : 10.), sin)
