minz = minimum(zs)
maxz = maximum(zs)

ys = Float32[x[2] for x in positions];
miny = minimum(ys)
maxy = maximum(ys)

xs = Float32[x[1] for x in positions];
minx = minimum(xs)
maxx = maximum(xs)

colors1 = RGBA{Float32}[
 RGBA( (zs[i]-minz)/(maxz-minz),
        (xs[i]-minx)/(maxx-minx),
        (ys[i]-miny)/(maxy-miny), 0.5f0)
        for i in 1:length(inner)];

colors2 = RGBA{Float32}[
