function parallel(N)
    resistors = make_n(:Resistor, N, Symbol.("r", 1:N))
    @named source = ConstantVoltage(V=V)
    @named ground = Ground()

r_eqs = [
        [connect_pins(source.p, resistors[i].p) for i in 1:N]...
        [connect_pins(resistors[i].n, ground.g) for i in 1:N]...
        ]

    @named r_model = ODESystem(r_eqs, t,
                                systems=[resistors..., source, ground])

    structural_simplify(r_model)
end
