using JuMP, Gurobi
const GRB_ENV = Gurobi.Env()
model = Model(() -&gt; Gurobi.Optimizer(GRB_ENV))
@variable(model,x_kij[k in 1:2,i in 1:11,j in 1:12], Bin);
@constraint(model,con_5[ i=2:11,  j=i+1:11;  i!=j],sum(x_kij[k,i,j]+x_kij[k,j,i] for k=1:2)&lt;=1)
MOI.set(model, Gurobi.ConstraintAttribute("Lazy"), con_5, 1)

