function fast_bin(v::Union{Float64, Quantity}, bin_size::Union{Float64, Quantity})
    bin = Int64(floor(abs(v) / bin_size) + 1)
    # bin = Int64(floor(abs(v) / bin_size) + 1)
    edge = bin * bin_size
    if edge < v
        # left bin
        bin += 2
        if v > 0unit(v)
            return Bin(bin)
        else
            return Bin(-bin)
        end
    else
        bin += 1
        if v > 0unit(v)
            return Bin(bin)
        else
            return Bin(-bin)
        end
    end
end
