using CBinding

@ctypedef AD @cstruct A {
  a::Cint
}
A(x::Number) = (a = A(undef); a.a = x; a)

@ctypedef BD @cstruct B {
  p::Ptr{A}
}
B(a::Ptr) = (b = B(undef); b.p = a; b)

a = A(111)
b = B(convert(Ptr{A}, pointer_from_objref(a)))
c = unsafe_load(b.p) # insert magic here
c.a = 987
@assert c.a == a.a
