import Base.:<

function Base.:<(x::nf_elem,y::nf_elem)
    @assert parent(x) == parent(y)
    field = parent(x)
    @assert istotally_real(field)
    places = infinite_places(field)

    if x-y == 0
       return false
    end
    return isnegative(x-y,places[1])
end
